mapper.select:
SELECT a.id,a.name,a.passowrd,a.client_id,a.platform_id,a.status,a.remark,a.update_date,a.insert_date, c.name as clientName,g.name as platformName,u1.name as updateUser,u2.name as insertUser FROM accounts a LEFT JOIN clients c ON a.client_id = c.id LEFT JOIN gaming_platform g ON g.id = c.platform_id LEFT JOIN users u1 on a.update_user = u1.id LEFT JOIN users u2 on a.insert_user = u2.id
1=1
AND name LIKE CONCAT('%',#{name},'%')
AND client_id = #{clientId}
AND platform_id = #{platformId}
AND c.name LIKE CONCAT('%',#{clientName},'%'}

mapper.delete:
UPDATE accounts SET
  status = #{status},
  update_date = #{updateDate},
  update_user = #{updateUser}
  WHERE id = #{id}
