mapper.select:
SELECT d.platform_id,d.game_id,d.point,d.status,d.update_date,d.insert_date,p.name as platformName,g.name as gameName,
u1.name as updateUser,u2.name as insertUser
FROM discount d LEFT JOIN gaming_platform p ON d.platform_id = p.id
LEFT JOIN game g ON g.id = d.game_id
LEFT JOIN users u1 on d.update_user = u1.id
LEFT JOIN users u2 on d.insert_user = u2.id


        d.platform_id = #{platformId}


        d.gamg_id = #{gameId}


        d,status = #{status}

mapper.update:
UPDATE discount SET
point = #{point},
update_date = #{updateDate},
update_user = #{updateUser}
  WHERE platform_id = #{platformId} AND game_id = #{gameId}

UPDATE discount SET
status = #{status},
update_date = #{updateDate},
update_user = #{updateUser}
  WHERE platform_id = #{platformId} AND game_id = #{gameId}
