mapper.update:
UPDATE game_platform SET
name = #{name},
remark = #{remark},
update_date = #{updateDate},
update_user = #{updateUser}
WHERE id = #{id}

UPDATE game_platform SET
status = 0,update_user = #{userId}
WHERE id = #{id}

UPDATE game_platform SET
status = 0,update_user = #{userId}
WHERE
id IN

    #{id}

mapper.select:
SELECT tr.id,tr.name,tr.status FROM game_platform tr
tr.status = 1
AND tr.name = #{search}

SELECT tr.id,tr.name
FROM game_platform tr,

    t_cosler c${index}

WHERE tr.status = 1 AND

    CONV(HEX(substring(tr.name,${index+1},1)),16,10) between c${index}.cBegin and c${index}.cEnd AND
    c${index}.f_PY = #{item}
