mapper.select:
SELECT id,name,role,status,update_date as updateDate,insert_date as insertDate
FROM users
WHERE id=#{id}

SELECT id,name,role,status,update_date as updateDate,insert_date as insertDate
FROM users

    1=1

      AND  status = #{status}


        AND name LIKE CONCAT('%',#{name},'%')


        AND role = #{role}

mapper.update:
UPDATE users SET
  status = #{status}
  WHERE id = #{id}
